# [300] 最长上升子序列
#

# @lc code=start
class Solution:
    def lengthOfLIS(self, nums) -> int:
        n = len(nums)

        if n<=1:
            return n
        dp =[1 for _ in range(n)]
        ret = 0
        for i in range(1, n):
            for j in range(0, i):
                dp[i] = max(dp[i], dp[j]+1 if nums[i]>nums[j] else 1)
            ret = max(ret, dp[i]) 
        return ret

    def lengthOfLIS(self, nums):
        n = len(nums)
        if not nums:
            return 0
        tails = [nums[0]]
        for num in nums[1:]:
            if num>tails[-1]:
                tails.append(num)
                continue
            l, r = 0, len(tails)-1
            while(l<r):
                mid = l+(r-l)//2
                if tails[mid]<num:
                    l = mid+1
                else:
                    r = mid
            tails[l]=num
        return len(tails)

